import { createGlobalStyle } from 'styled-components';
import background from '../assets/images/background.svg';

export default createdGlobalStyle`
@import url('https://fonts.googleapis.com/css?family=Josefin+Sans:300,400,600,600i,700&display=swap');
  * {
    margin: 0;
    padding: 0;
    outline:0;
    box-sizing: boder-box;
  }

  body {
    background: #191919 url({background}) no-repeat center top;
    -webkit-font-smoothing: antialiased;
  }

  body, input, button {
    font: 14px Josefin Slab, sans-serif;
  }

  #root {
    max-width: 1020px;
    margin: 0 auto;
    padding: 0 20px 50px;
  }

  button{
    cursor: pointer;
  }

`;
